import React from 'react'
import {connect} from 'react-redux'
import ReactLoading from 'react-loading'

import {
  getIsSearchPressed
} from 'selectors'

import './insex.sass'

const Banner = ({isSearchPressed}) => {
  let load
  if (isSearchPressed !== true) {
    load = true
  }
  return (
    <div className='banner'>
      {
        load === true ? 
        <div className='banner-loading'>
          <ReactLoading type='spinningBubbles' color='#10ACC2' height={200} width={200} />
          <span className='banner-loading__desc'>Search for recipes please wait...</span>
        </div>:
        <div className='banner-item'></div>
      }
    </div>
  )
}

const mapStateToProps = state => ({
  isSearchPressed: getIsSearchPressed(state),
})

export default connect(mapStateToProps, null)(Banner);