import React from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {
  getTotalBasketCount,
  getTotalBasketTime,
  getTotalBasketCalories
} from 'selectors'

// import './index.css'
import './index.sass'

const BasketCart = ({totalBasketCount, totalTime, totalCalories}) => (
  <div className='cart'>
    <h3 className='basket-title'>Total time cooking</h3>
    <span className='cart-result'>{totalBasketCount} recipe(s) - {totalTime}</span>
    <span className='cart-result'>{totalCalories} calories</span>
    <Link
      to='/basket'
      id='dLabel'
      className='btn-basket'
    >
      Go to chosen list
    </Link>
  </div>
)

const mapStateToProps = state => ({
  totalBasketCount: getTotalBasketCount(state),
  totalTime: getTotalBasketTime(state),
  totalCalories: getTotalBasketCalories(state)
})

export default connect(mapStateToProps, null)(BasketCart);