import React from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {
    getTotalBasketCount,
    getTotalBasketTime
} from 'selectors'

// import './index.css'
import './index.sass'

const BasketCart = ({totalBasketCount, totalTime}) => (
    <div className='cart'>
        <div className='dropdown'>
            <h3 className='basket-title'>Total time cooking</h3>
            <Link
                to='/basket'
                id='dLabel'
                className='btn-basket'
            >
                <span className='btn-basket__desc'>
                    {totalBasketCount} recipe(s) - {totalTime} min
                </span>
            </Link>
        </div>
    </div>
)

const mapStateToProps = state => ({
    totalBasketCount: getTotalBasketCount(state),
    totalTime: getTotalBasketTime(state)
})

export default connect(mapStateToProps, null)(BasketCart);