import React from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'

import {
  getTotalBasketCount,
  getTotalBasketTime,
  getTotalBasketCalories
} from 'selectors'

// import './index.css'
import './index.sass'

const BasketCart = ({totalBasketCount, totalTime, totalCalories}) => (
  <div className='cart'>
    <h3 className='cart-title'>Total time cooking</h3>
    <div className='cart-info'>
      <span className='cart-info__item'>{totalBasketCount} recipe(s) - {totalTime}</span>
      <span className='cart-info__item'>{totalCalories} calories</span>
    </div>
    <Link
      to='/basket'
      id='dLabel'
      className='btn-cart'
    >
      Go to chosen list
    </Link>
  </div>
)

const mapStateToProps = state => ({
  totalBasketCount: getTotalBasketCount(state),
  totalTime: getTotalBasketTime(state),
  totalCalories: getTotalBasketCalories(state)
})

export default connect(mapStateToProps, null)(BasketCart);