import React, { Component } from 'react'
// import {Link} from 'react-router'
import {connect} from 'react-redux'

import {fetchRecipes} from 'actions'

// import './index.css'
import './index.sass'

class SearchByIngredients extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: ''
        }

        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    handleSubmit (event) {
        event.preventDefault()
        this.props.fetchRecipes(this.state.value)
    }

    handleChange (event) {
        this.setState({
            value: event.target.value
        })
    }

    render () {
        return (
            <div className='search-ingredients'>
                <div className='search-ingredients__enter'>
                    <h3 className='search-ingredients__title'>Search by ingredients</h3>
                    <form onSubmit={this.handleSubmit}>
                        <div className='search-ingredients__block'>
                            <input
                                onChange={this.handleChange}
                                type='text'
                                className='search-ingredients__input'
                                placeholder='eggs, tomato paste'
                            />
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

const mapDispatchToProps = {
    fetchRecipes
}

export default connect(null, mapDispatchToProps)(SearchByIngredients)