import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router'

import {
  loadMoreRecipes,
  addRecipeToBasket,
} from 'actions'
import {getRecipes} from 'selectors'

import Banner from 'components/banner'

// import './index.css'
import './index.sass'

class Recipes extends Component {

    renderBanner () {
        return (
            <div>
                <Banner />
            </div>
        )
    }

    renderIngredientsList (ingredients) {
        const newArr = (list) => {
            let arr = []
            for (let i = 0; i < 5 && i < list.length; i++) {
                arr[i] = list[i]
            }
            return arr
        }

        const newIngredientsList = newArr(ingredients)

        return newIngredientsList.map((value, key) => {
            return (
                <div 
                    className='recipes-ingredients__list' 
                    key={key}
                >
                    <div>{value}</div>
                </div>
            )
        })
    }

    renderRecipe (recipe, index) {
        const {addRecipeToBasket} = this.props
        return (
            <div className='recipes-block' key={index}>
                <div className='recipes-title'>
                    <h4 className='recipes-title__link'>
                        <Link 
                            to={`/recipes/${recipe.id}`}
                            className='recipes-link'
                        >
                            {recipe.recipeName}
                        </Link>
                    </h4>
                </div>
                <div className='recipes-body'>
                    <div className='recipes-info'>
                        <div>
                            <img
                                src={recipe.imageUrlsBySize[90]}
                                alt={recipe.recipeName}
                                className='recipes-image'
                            />
                        </div>
                        <div className='recipes-ingredients'>
                            <h4
                                className='recipes-ingredients__title'
                            >
                                Ingredients
                            </h4>
                            {this.renderIngredientsList(recipe.ingredients)}
                            <div className='recipes-ingredients__more'>
                                <p>...</p>
                            </div>
                        </div>
                    </div>
                    <div className='recipes-time-btn'>
                        <div className='recipes-time'>
                            <div className='recipes-time__item'>
                                <span>Cooking time: </span>
                            </div>
                            <div className='recipes-time__item'>
                                <span>
                                    {recipe.totalTimeInSeconds/60} min
                                </span>
                            </div>
                        </div>
                        <div className='item-button'>
                            <button 
                                className='btn-add-busket'
                                onClick={() => addRecipeToBasket(recipe.id)}
                            >
                                Add to basket
                            </button>
                            <Link 
                                to={`/recipes/${recipe.id}`}
                                className='btn-info'
                            >
                                More info
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    renderLoadMore (loadMoreRecipes) {
        return (
            <div>
                <button 
                    onClick={loadMoreRecipes}
                    className='recipes-loadmore__btn'
                >
                    Load more
                </button>
            </div>
        )
    }

    render () {
        const {recipes, loadMoreRecipes} = this.props
        return (
            <div>
                <div className='recipes-list'>
                {
                    recipes.length !== 0 ?
                    recipes.map((recipe, index) => this.renderRecipe(recipe, index)) :
                    this.renderBanner()
                }
                </div>
                <div className='recipes-loadmore'>
                    {recipes.length !== 0 && this.renderLoadMore(loadMoreRecipes)}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        recipes: getRecipes(state)
    }
}

const mapDispatchToProps = {
    loadMoreRecipes,
    addRecipeToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Recipes)