import * as R from 'ramda'

import {
    ADD_RECIPE_TO_BASKET,
    REMOVE_RECIPE_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_RECIPE_TO_BASKET:
            for (let i = 0; i < state.length; i++) {
                if (state[i] === payload) {
                    return state
                }
            }

            return R.append(payload, state)
        case REMOVE_RECIPE_FROM_BASKET:
            return R.without(R.of(payload), state)
        case CLEAN_BASKET:
            return []            
        default:
            return state
    }
}