import * as R from 'ramda'

import {
    ADD_RECIPE_TO_BASKET_SUCCESS,
    REMOVE_RECIPE_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_RECIPE_TO_BASKET_SUCCESS:
            return R.assoc(payload.id, payload, state)
        case REMOVE_RECIPE_FROM_BASKET:
            return R.dissoc(payload, state)
        case CLEAN_BASKET:
            return {}            
        default:
            return state
    }
}