import * as R from 'ramda'

import {
    BASKET_CHECKOUT_SUCCESS
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case BASKET_CHECKOUT_SUCCESS:
            const newValues = R.indexBy(R.prop('id'), payload)
            return R.merge(state, newValues)
        default:
            return state
    }
}