import * as R from 'ramda'

import {
    FETCH_RECIPE_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_RECIPE_BY_ID_SUCCESS:
            return R.assoc(payload.id, payload, state)
        default:
            return state
    }
}